(function ($, Drupal) {
  $(function(){



    $(document).bind('cbox_complete', function(){
      $('.closethiscolorbox').click(function(){
        $.colorbox.remove(); // You have to remove it first (don't know why)
        $('#cboxClose').click(); // Then this will close the box, $.colorbox.close() still doesn't work
      });
    });

    $.urlParam = function(name){
      var results = new RegExp('[\?&]' + name + '=([^&#]*)').exec(window.location.href);
      if (results==null){
       return null;
      }
      else{
       return results[1] || 0;
      }
    };




    function splash() {

      var redirectSource = $.urlParam('redirect');

      switch (redirectSource) {
      case 'FestivalKortrijk':
        var cookieFK = Cookies.get('FestivalKortrijk');
        if(cookieFK != "true"){
          Cookies.set('FestivalKortrijk', 'true', { expires: 365, path: '' });
          $('a.colorbox-node').once('a.colorbox-node', function(){
            $(this).trigger('click');
          });
        }
        break;
      case 'DeKreun':
        var cookieDK = Cookies.get('DeKreun');
        if(cookieDK != "true"){
          Cookies.set('DeKreun', 'true', { expires: 365, path: '' });
          $('a.colorbox-node').once('a.colorbox-node', function(){
            $(this).trigger('click');
          });
        }
        break;
      case 'SonicCity':
        var cookieSC = Cookies.get('SonicCity');
        if(cookieSC != "true"){
          Cookies.set('SonicCity', 'true', { expires: 365, path: '' });
          $('a.colorbox-node').once('a.colorbox-node', function(){
            $(this).trigger('click');
          });
        }
        break;
      }
    }

    splash();

    function mobilemenu() {
      var $nav = $('.mobile-menu');
      var $header = $('#header');
      var $navMenu = $nav.find('.menu');

      if($(window).width() < 768 && (!$nav.hasClass('menu-mobile'))) {

        $nav.addClass('menu-mobile');
        // Add toggle list item before navigation items
        $nav.before('<div class="toggle toggle-navigation"><a class="toggle__link toggle-navigation__link" href="#">Menu</a></div>');

        $header.find('.toggle__link').click(function(event) {
          event.preventDefault();
          // On click, show / hide the non active menu options
          $(this).toggleClass('toggle__link--open toggle-navigation__link--is-open').closest('.toggle').toggleClass('toggle--open toggle-navigation--is-open').find('.toggle__link').closest($nav).find('.menu').toggleClass('menu--is-open');
        });
      }

      else if($(window).width() >= 768 && ($nav.hasClass('menu-mobile'))) {
        // Remove toggle list item when mobile
        $nav.removeClass('menu-mobile');
        $('.toggle-navigation').remove();
      }
    }

    mobilemenu();

    if ($(window).width() >= 768){
      $('.group-ticket-fixed-block').stick_in_parent();
      //$('.node-info-page .group-right ').stick_in_parent();
    }

    $(window).resize(function(){
      mobilemenu();

      if ($(window).width() >= 768){
        $('.group-ticket-fixed-block').stick_in_parent();
        //$('.node-info-page .group-right ').stick_in_parent();
      }
    });


    $('#block-block-4').click(function(){
      $(this).toggleClass( "toggle-open" );
      $(".search").toggleClass( "search-open" );
    });

    function sticky_relocate() {
      var window_top = $(window).scrollTop();

      if (window_top > 45) {
        $('#page-wrapper').addClass("fixed-header-first");

      } else {
        $('#page-wrapper').removeClass("fixed-header-first");
      }

      if (window_top > 20 && window_top < 80) {
        $('.search-open').addClass("search-no-animations");
      } else {
        $('.search-open').removeClass("search-no-animations");
      }
    }

    $(function () {
      $(window).scroll(sticky_relocate);
      sticky_relocate();
    });

    $('.event-full-images').slick({
      autoplay: true,
      infinite: true,
      speed: 1000
    });
  });

})(jQuery, Drupal);
